mapper.select:
			    SELECT  SEQUENCE 
                       ,CONCAT( DATE_FORMAT(PRC_DATE, #{condition.locale} ) ,' ',DATE_FORMAT(now(), '%H:%i:%s') ) AS PRC_DATE
			           ,NODE_NAME AS SYSTEM
			         
						
						, ${viewColumn}
			  		    
			  		 
			         
			         	
				 		, ${sumColumn}
				 		
			  		 
			    FROM (       
					    
					                                        
						
						ORDER BY ${condition.orderBy}
						
						
						ORDER BY PRC_DATE , NODE_NAME ASC
						
					    
			         )RerFormList

	    SELECT  SEQUENCE 
			   ,CONCAT( DATE_FORMAT(PRC_DATE, #{condition.locale} ) ,' ',DATE_FORMAT(now(), '%H:%i:%s') ) AS PRC_DATE
	           ,NODE_NAME AS SYSTEM
	         
				
				, ${viewColumn}
	  		    
	  		 
	         
	         	
		 		, ${sumColumn}
		 		
	  		 
	    FROM ( 
  	                                                
				
				ORDER BY ${condition.orderBy}
				
				
				ORDER BY PRC_DATE , NODE_NAME ASC
				
	         )RerFormList

mapper.sql:
	     SELECT  SEQUENCE
		       , PRC_DATE
    	       , NODE_NAME
			  
				
				, ${viewColumn}
	  		    
		  	  
	          
	         	 
		 		 ,${sumColumn}
		 		 
	  		  
		FROM  ${condition.tableName} 
		      WHERE 1=1
	 	        AND NODE_NAME IN
						
							 #{systemArray}
						
		      
		      
		          		${condition.whereClause}
